import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import {ToastContainer} from "react-toastify"

const SignUp = () => {


    const[formData,setFormData]=useState(
        {firstName:" ",
        lastName:"",
        eMail:"",
        comment:"",
        checkbox:"true",
        mode:""
    
      });
      console.log(formData)
    
      function changehandler(event){
        setFormData(prevFormData=>{
          return{
            ...prevFormData,
            [event.target.name]:event.target.value
          }
        });
        
      }

      function submitHandler(event){
        event.preventDefault();
        console.log("finally prinitting the data");
        console.log(formData)
        alert("done");
    
      }
    
      


    
  return (
    <div className= 'container bg-white px-20 py-6 rounded-lg max-w-[500px] shadow-lg shadow-gray-900 '>
        
       <form onSubmit={submitHandler}  >

       <h1 className='text-6xl mb-10 font-bold'>signup</h1>
        <div >
            <label htmlFor="">name</label>
            <input type="text" 
            placeholder='enter name'
             name='name'
             onChange={changehandler}
             value={formData.name}
              />
        </div>
        <div>
            <label htmlFor="">email</label>
            <input type="text" placeholder='enter name' name='email' onChange={changehandler}
 />
        </div>
        <div>
            <label htmlFor="">password</label>
            <input type="password" placeholder='enter your password' name='password'              onChange={changehandler}
 />
        </div>
        <button>signup</button> <br />
        <span>already have an account ? <Link to='/login'>login</Link> </span>
       </form>
       <ToastContainer/>
        
    </div>
  )
}

export default SignUp
